<style lang="scss">
.phone-icon {
    user-select: none;
    &:focus {
        animation-name: phone-call;
        animation-duration: 300ms;
        animation-iteration-count: 1;
    }
}

@keyframes phone-call {
    0% {
        transform: rotate(0deg);
    }
    25% {
        transform: rotate(20deg);
    }
    50% {
        transform: rotate(-20deg);
    }
    100% {
        transform: rotate(0deg);
    }
}
</style>
